import { useLayoutEffect, useRef } from "react";

const Quote = ({ author, quote }) => {

  useLayoutEffect(() => {
    const pRef = useRef()
  }, [quote])

  return (
    <blockquote
      className="blockquote text-end"
      style={{ display: 'flex' }}
    >
      <p className="mb-1">{quote}</p>
      <footer className="blockquote-footer mt-2">{author}</footer>
    </blockquote>
  )
}

export default Quote
