import { useState } from "react"

const useForm = ( initialForm = {} ) => {

  const [formState, setFormState] = useState( initialForm )

  const handleChange = ({ target }) => {
    const { name, value } = target
    setFormState({
      ...formState,
      [name]: value
    })
  }

  return {
    ...formState, 
    formState,
    handleChange,
  }
}

export default useForm
