import { useEffect, useState } from "react"

const useFetch = (url) => {

  const [state, setState] = useState({
    data: null,
    isLoading: true,
    hasError: null,
  })

  const getFetch = async () => {
    const resp = await fetch(url)
    const data = await resp.json()

    setState({
      data,
      isLoading: false,
      hasError: null,
    })
  }

  useEffect(() => {
    getFetch()
  },[url])

  return {

  }
}

export default useFetch
